            mumuFrensNFT: 0x0000000000000000000000000000000000000000, // To be bridged/deployed
            gmxRouter: 0xaBBc5F99639c9B6bCb58544ddf04EFA6802F4064,
            uniswapRouter: 0xE592427A0AEce92De3Edee1F18E0157C05861564,
