            arbitrum: 4949039107694359620
        });
    }
}

project/contracts/Harpoon.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";

// Trading platform interfaces
interface IGMXRouter {
    function createIncreasePosition(
        address[] memory _path,
        address _indexToken,
        uint256 _amountIn,
        uint256 _minOut,
        uint256 _sizeDelta,
        bool _isLong,
        uint256 _acceptablePrice
    ) external payable;
    
    function createDecreasePosition(
        address[] memory _path,
        address _indexToken,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        bool _isLong,
        address _receiver,
        uint256 _acceptablePrice,
        uint256 _minOut
    ) external payable;
}

interface IUniswapV3Router {
    struct ExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
        uint160 sqrtPriceLimitX96;
    }
    
    function exactInputSingle(ExactInputSingleParams calldata params)
        external
        payable
        returns (uint256 amountOut);
}

/**
 * @title Harpoon
 * @notice Individual trading position contract supporting GMX and Uniswap
 * @dev Supports creator closure, NFT holder voting, and automated liquidation
 */
contract Harpoon is Initializable, ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // 
    //                             CONSTANTS
    // 
    
    uint256 public constant VOTE_DURATION = 7 days;
    uint256 public constant MIN_VOTES_FOR_CLOSURE = 3;
    uint256 public constant LIQUIDATION_THRESHOLD_BPS = 8500; // 85% loss triggers liquidation
    uint256 public constant MAX_BPS = 10000;

    // 
    //                             ENUMS
    // 
    
    enum Status { Pending, Open, Closed, Liquidated }
    enum Platform { GMX, UNISWAP }
    enum CloseReason { Creator, Vote, Liquidation, Expiry }

    // 
    //                             STRUCTS
    // 
    
    struct PositionParams {
        address targetToken;        // Token being traded
        uint256 collateralAmount;   // Initial collateral
        uint256 leverage;           // Leverage multiplier
        bool isLong;               // Long or short position
        uint256 slippageBps;       // Max slippage tolerance
        string platformName;       // "GMX" or "UNISWAP"
        uint256 duration;          // Max position duration
        bytes platformSpecificData; // Platform-specific parameters
    }
    
    struct Position {
        Platform platform;
        address market;            // Market/pair address
        uint256 entryPrice;       // Entry price (scaled appropriately)
        uint256 exitPrice;        // Exit price (0 if not closed)
        uint256 collateral;       // Actual collateral used
        uint256 size;             // Position size
        uint256 openTimestamp;    // When position was opened
        uint256 closeTimestamp;   // When position was closed (0 if open)
        int256 pnl;               // Profit/loss (signed integer)
    }
    
    struct Vote {
        uint256 startTime;        // Vote start timestamp
        uint256 endTime;         // Vote end timestamp
        uint256 yesVotes;        // Number of yes votes
        uint256 noVotes;         // Number of no votes
        bool executed;           // Whether vote result was executed
        mapping(uint256 => bool) hasVoted; // tokenId => has voted
    }

    // 
    //                             STORAGE
    // 
    
    // Core contract state
    address public factory;
    address public creator;
    Status public status;
    
    // Position details
    PositionParams public params;
    Position public position;
    
    // NFT contracts for voting
    IERC721 public surfBoardNFT;
    IERC721 public mumuFrensNFT;
    
    // Trading integration
    address public platformRouter;
    
    // Voting mechanism
    Vote public currentVote;
    bool public voteInProgress;
    
    // Financial tracking
    uint256 public initialCollateral;
    uint256 public currentValue;
    CloseReason public closeReason;

    // 
    //                             EVENTS
    // 
    
    event PositionOpened(
        Platform platform,
        address market,
        uint256 collateral,
        uint256 leverage,
        bool isLong
    );
    
    event PositionClosed(
        address indexed closer,
        CloseReason reason,
        int256 pnl,
        uint256 finalValue
    );
    
    event VoteStarted(uint256 startTime, uint256 endTime);
    event VoteCast(address indexed voter, uint256 indexed tokenId, bool support);
    event VoteExecuted(bool approved, uint256 yesVotes, uint256 noVotes);
    event FundsWithdrawn(address indexed to, uint256 amount);
    event EmergencyLiquidation(uint256 losses, uint256 remaining);

    // 
    //                             MODIFIERS
    // 
    
    modifier onlyCreator() {
        require(msg.sender == creator, "Not creator");
        _;
    }
    
    modifier onlyFactory() {
        require(msg.sender == factory, "Not factory");
        _;
    }
    
    modifier onlyNFTHolder() {
        require(_ownsEligibleNFT(msg.sender), "Not NFT holder");
        _;
    }
    
    modifier onlyOpenPosition() {
        require(status == Status.Open, "Position not open");
        _;
    }
    
    modifier notExpired() {
        require(
            block.timestamp <= position.openTimestamp + params.duration,
            "Position expired"
        );
        _;
    }

    // 
    //                             CONSTRUCTOR
    // 
    
    /**
     * @notice Constructor (used only for implementation)
     */
    constructor() Ownable(address(0)) {
        _disableInitializers();
    }
    
    /**
     * @notice Initialize the Harpoon (called by factory)
     * @param _creator Position creator
     * @param _surfBoardNFT SURF Board NFT contract
     * @param _mumuFrensNFT mumu-frens NFT contract
     * @param _params Position parameters
     * @param _platformRouter Trading platform router
     */
    function initialize(
        address _creator,
        address _surfBoardNFT,
        address _mumuFrensNFT,
        PositionParams calldata _params,
        address _platformRouter
    ) external initializer {
        require(_creator != address(0), "Invalid creator");
        require(_surfBoardNFT != address(0), "Invalid SURF Board NFT");
        require(_mumuFrensNFT != address(0), "Invalid mumu-frens NFT");
        require(_platformRouter != address(0), "Invalid platform router");
        
        factory = msg.sender;
        creator = _creator;
        surfBoardNFT = IERC721(_surfBoardNFT);
        mumuFrensNFT = IERC721(_mumuFrensNFT);
        platformRouter = _platformRouter;
        params = _params;
        status = Status.Pending;
        initialCollateral = _params.collateralAmount;
        
        _transferOwnership(_creator);
    }

    // 
    //                          POSITION MANAGEMENT
    // 
    
    /**
     * @notice Open the trading position
     * @dev Can only be called once by creator
     */
    function openPosition() external onlyCreator nonReentrant {
        require(status == Status.Pending, "Position already opened");
        
        // Determine platform
        Platform platform;
        if (keccak256(bytes(params.platformName)) == keccak256(bytes("GMX"))) {
            platform = Platform.GMX;
        } else if (keccak256(bytes(params.platformName)) == keccak256(bytes("UNISWAP"))) {
            platform = Platform.UNISWAP;
        } else {
            revert("Unsupported platform");
        }
        
        // Get collateral from creator
        IERC20 collateralToken = IERC20(params.targetToken); // Simplified - should handle USDC
        collateralToken.safeTransferFrom(creator, address(this), params.collateralAmount);
        
        // Open position based on platform
        if (platform == Platform.GMX) {
            _openGMXPosition();
        } else {
            _openUniswapPosition();
        }
        
        // Update state
        position.platform = platform;
        position.collateral = params.collateralAmount;
        position.openTimestamp = block.timestamp;
        status = Status.Open;
        currentValue = params.collateralAmount;
        
        emit PositionOpened(
            platform,
            position.market,
            params.collateralAmount,
            params.leverage,
            params.isLong
        );
    }
    
    /**
     * @notice Close position by creator
     */
    function closePositionByCreator() external onlyCreator onlyOpenPosition nonReentrant {
        _closePosition(CloseReason.Creator);
    }
    
    /**
     * @notice Emergency liquidation if position is underwater
     */
    function liquidatePosition() external onlyOpenPosition nonReentrant {
        require(_shouldLiquidate(), "Position not liquidatable");
        _closePosition(CloseReason.Liquidation);
    }
    
    /**
     * @notice Close expired position
     */
    function closeExpiredPosition() external onlyOpenPosition nonReentrant {
        require(
            block.timestamp > position.openTimestamp + params.duration,
            "Position not expired"
        );
        _closePosition(CloseReason.Expiry);
    }

    // 
    //                          VOTING MECHANISM
    // 
    
    /**
     * @notice Start a vote to close the position
     * @dev Can be called by any NFT holder
     */
    function startVoteToClose() external onlyNFTHolder onlyOpenPosition {
        require(!voteInProgress, "Vote already in progress");
        
        currentVote.startTime = block.timestamp;
        currentVote.endTime = block.timestamp + VOTE_DURATION;
        currentVote.yesVotes = 0;
        currentVote.noVotes = 0;
        currentVote.executed = false;
        voteInProgress = true;
        
        emit VoteStarted(currentVote.startTime, currentVote.endTime);
    }
    
    /**
     * @notice Cast vote to close position
     * @param tokenId NFT token ID being used to vote
     * @param support True for yes, false for no
     */
    function voteClose(uint256 tokenId, bool support) external nonReentrant {
        require(voteInProgress, "No vote in progress");
        require(block.timestamp <= currentVote.endTime, "Voting period ended");
        require(!currentVote.hasVoted[tokenId], "Token already voted");
        require(_ownsNFTToken(msg.sender, tokenId), "Not token owner");
        
        currentVote.hasVoted[tokenId] = true;
        
        if (support) {
            currentVote.yesVotes++;
        } else {
            currentVote.noVotes++;
        }
        
        emit VoteCast(msg.sender, tokenId, support);
    }
    
    /**
     * @notice Execute vote result
     */
    function executeVote() external nonReentrant {
        require(voteInProgress, "No vote in progress");
        require(block.timestamp > currentVote.endTime, "Voting still active");
        require(!currentVote.executed, "Vote already executed");
        
        currentVote.executed = true;
        voteInProgress = false;
        
        bool approved = currentVote.yesVotes > currentVote.noVotes && 
                       currentVote.yesVotes >= MIN_VOTES_FOR_CLOSURE;
        
        emit VoteExecuted(approved, currentVote.yesVotes, currentVote.noVotes);
        
        if (approved) {
            _closePosition(CloseReason.Vote);
        }
    }

    // 
    //                         INTERNAL FUNCTIONS
    // 
    
    /**
     * @notice Open GMX position
     */
    function _openGMXPosition() internal {
        IGMXRouter router = IGMXRouter(platformRouter);
        
        // Approve router to spend collateral
        IERC20(params.targetToken).forceApprove(platformRouter, params.collateralAmount);
        
        // Prepare path (simplified - should be more sophisticated)
        address[] memory path = new address[](1);
        path[0] = params.targetToken;
        
        // Calculate size delta based on leverage
        uint256 sizeDelta = params.collateralAmount * params.leverage;
        
        // Calculate acceptable price with slippage
        uint256 acceptablePrice = _calculateAcceptablePrice();
        
        // Create position
        router.createIncreasePosition(
            path,
            params.targetToken,
            params.collateralAmount,
            0, // minOut
            sizeDelta,
            params.isLong,
            acceptablePrice
        );
        
        position.market = params.targetToken;
        position.size = sizeDelta;
        position.entryPrice = acceptablePrice;
    }
    
    /**
     * @notice Open Uniswap position
     */
    function _openUniswapPosition() internal {
        IUniswapV3Router router = IUniswapV3Router(platformRouter);
        
        // Approve router
        IERC20(params.targetToken).forceApprove(platformRouter, params.collateralAmount);
        
        // For Uniswap, we'll do a simple swap (simplified implementation)
        IUniswapV3Router.ExactInputSingleParams memory swapParams = IUniswapV3Router.ExactInputSingleParams({
            tokenIn: params.targetToken,
            tokenOut: params.targetToken, // Would be different in real implementation
            fee: 3000, // 0.3%
            recipient: address(this),
            deadline: block.timestamp + 300,
            amountIn: params.collateralAmount,
            amountOutMinimum: _calculateMinOut(),
            sqrtPriceLimitX96: 0
        });
        
        uint256 amountOut = router.exactInputSingle(swapParams);
        
        position.market = params.targetToken;
        position.size = amountOut;
        position.entryPrice = (params.collateralAmount * 1e18) / amountOut;
    }
    
    /**
     * @notice Close position and settle
     * @param reason Reason for closure
     */
    function _closePosition(CloseReason reason) internal {
        require(status == Status.Open, "Position not open");
        
        // Close based on platform
        if (position.platform == Platform.GMX) {
            _closeGMXPosition();
        } else {
            _closeUniswapPosition();
        }
        
        // Calculate P&L
        currentValue = address(this).balance + IERC20(params.targetToken).balanceOf(address(this));
        position.pnl = int256(currentValue) - int256(initialCollateral);
        position.closeTimestamp = block.timestamp;
        closeReason = reason;
        
        // Update status
        if (reason == CloseReason.Liquidation) {
            status = Status.Liquidated;
        } else {
            status = Status.Closed;
        }
        
        emit PositionClosed(msg.sender, reason, position.pnl, currentValue);
    }
    
    /**
     * @notice Close GMX position
     */
    function _closeGMXPosition() internal {
        IGMXRouter router = IGMXRouter(platformRouter);
        
        address[] memory path = new address[](1);
        path[0] = params.targetToken;
        
        router.createDecreasePosition(
            path,
            params.targetToken,
            position.collateral,
            position.size,
            params.isLong,
            address(this),
            _calculateAcceptablePrice(),
            0
        );
    }
    
    /**
     * @notice Close Uniswap position
     */
    function _closeUniswapPosition() internal {
        // Simplified - would swap back to original token
        // Implementation depends on specific Uniswap strategy used
    }
    
    /**
     * @notice Check if position should be liquidated
     */
    function _shouldLiquidate() internal view returns (bool) {
        if (status != Status.Open) return false;
        
        // Simplified liquidation logic
        uint256 currentCollateralValue = _getCurrentValue();
        uint256 lossThreshold = (initialCollateral * LIQUIDATION_THRESHOLD_BPS) / MAX_BPS;
        
        return currentCollateralValue < lossThreshold;
    }
    
    /**
     * @notice Get current position value
     */
    function _getCurrentValue() internal view returns (uint256) {
        // Simplified - would query actual position value from platform
        return currentValue;
    }
    
    /**
     * @notice Calculate acceptable price with slippage
     */
    function _calculateAcceptablePrice() internal view returns (uint256) {
        // Simplified price calculation
        return 1e18; // Placeholder
    }
    
    /**
     * @notice Calculate minimum output for swaps
     */
    function _calculateMinOut() internal view returns (uint256) {
        uint256 slippageAmount = (params.collateralAmount * params.slippageBps) / MAX_BPS;
        return params.collateralAmount - slippageAmount;
    }
    
    /**
     * @notice Check if address owns eligible NFT
     */
    function _ownsEligibleNFT(address user) internal view returns (bool) {
        return surfBoardNFT.balanceOf(user) > 0 || mumuFrensNFT.balanceOf(user) > 0;
    }
    
    /**
     * @notice Check if address owns specific NFT token
     */
    function _ownsNFTToken(address user, uint256 tokenId) internal view returns (bool) {
        try surfBoardNFT.ownerOf(tokenId) returns (address owner) {
            if (owner == user) return true;
        } catch {}
        
        try mumuFrensNFT.ownerOf(tokenId) returns (address owner) {
            if (owner == user) return true;
        } catch {}
        
        return false;
    }

    // 
    //                           VIEW FUNCTIONS
    // 
    
    /**
     * @notice Get position details
     */
    function getPositionDetails() external view returns (
        Platform platform,
        uint256 collateral,
        uint256 size,
        uint256 entryPrice,
        uint256 openTime,
        int256 pnl
    ) {
        return (
            position.platform,
            position.collateral,
            position.size,
            position.entryPrice,
            position.openTimestamp,
            position.pnl
        );
    }
    
    /**
     * @notice Get current vote details
     */
    function getCurrentVote() external view returns (
        bool inProgress,
        uint256 startTime,
        uint256 endTime,
        uint256 yesVotes,
        uint256 noVotes,
        bool executed
    ) {
        return (
            voteInProgress,
            currentVote.startTime,
            currentVote.endTime,
            currentVote.yesVotes,
            currentVote.noVotes,
            currentVote.executed
        );
    }
    
    /**
     * @notice Check if token has voted
     */
    function hasTokenVoted(uint256 tokenId) external view returns (bool) {
        return currentVote.hasVoted[tokenId];
    }
    
    /**
     * @notice Get estimated current P&L
     */
    function getEstimatedPnL() external view returns (int256) {
        if (status != Status.Open) {
            return position.pnl;
        }
        
        uint256 currentPositionValue = _getCurrentValue();
        return int256(currentPositionValue) - int256(initialCollateral);
    }

    // 
    //                          FUND MANAGEMENT
    // 
    
    /**
     * @notice Withdraw funds after position is closed
     */
    function withdrawFunds() external onlyCreator nonReentrant {
        require(status == Status.Closed || status == Status.Liquidated, "Position still open");
        
        uint256 ethBalance = address(this).balance;
        uint256 tokenBalance = IERC20(params.targetToken).balanceOf(address(this));
        
        if (ethBalance > 0) {
            payable(creator).transfer(ethBalance);
        }
        
        if (tokenBalance > 0) {
            IERC20(params.targetToken).safeTransfer(creator, tokenBalance);
        }
        
        emit FundsWithdrawn(creator, ethBalance + tokenBalance);
    }
    
    /**
     * @notice Emergency function to recover stuck tokens
     */
    function emergencyRecoverToken(address token, uint256 amount) external onlyCreator {
        require(status == Status.Closed || status == Status.Liquidated, "Position still active");
        IERC20(token).safeTransfer(creator, amount);
    }
    
    /**
     * @notice Receive ETH
     */
    receive() external payable {}
}

project/contracts/HarpoonFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "./Harpoon.sol";
import "./CCIPBridgeContract.sol";

/**
 * @title HarpoonFactory
 * @notice Deploys and manages Harpoon trading contracts on Arbitrum
 * @dev Handles CCIP cross-chain calls and NFT-gated access
 */
contract HarpoonFactory is Ownable, ReentrancyGuard, Pausable, ICCIPReceiver {
    
    // 
    //                             CONSTANTS
    // 
    
    uint256 public constant MAX_HARPOONS_PER_USER = 10;
    uint256 public constant MIN_COLLATERAL = 100e6; // $100 USDC
    uint256 public constant MAX_LEVERAGE = 20;
    uint256 public constant MAX_DURATION = 30 days;

    // 
    //                             STORAGE
    // 
    
    // CCIP and cross-chain
    ICCIPRouter public immutable ccipRouter;
    uint64 public immutable ethereumChainSelector;
    address public trustedEthereumSender;
    
    // NFT contracts for access control
    IERC721 public immutable surfBoardNFT;
    IERC721 public immutable mumuFrensNFT;
    
    // Harpoon management
    address public harpoonImplementation;
    uint256 public harpoonCount;
    mapping(uint256 => address) public harpoons;
    mapping(address => uint256[]) public userHarpoons;
    mapping(address => uint256) public userHarpoonCount;
    
    // Fee structure
    uint256 public creationFee = 0.001 ether; // ARB fee for creating harpoons
    address public feeRecipient;
    
    // Platform integrations
    mapping(string => bool) public supportedPlatforms;
    mapping(string => address) public platformRouters;

    // 
    //                             STRUCTS
    // 
    
    struct HarpoonParams {
        address targetToken;        // Token to trade
        uint256 collateralAmount;   // Collateral in USDC
        uint256 leverage;           // Leverage multiplier (1-20)
        bool isLong;               // Long or short position
        uint256 slippageBps;       // Max slippage in basis points
        string platform;           // "GMX" or "UNISWAP"
        uint256 duration;          // Max position duration in seconds
        bytes platformSpecificData; // Additional platform-specific parameters
    }
    
    struct CCIPMessage {
        address sender;            // Ethereum sender
        bytes4 selector;          // Function selector
        bytes data;               // Encoded parameters
        uint256 nonce;            // Prevent replay
    }

    // 
    //                             EVENTS
    // 
    
    event HarpoonCreated(
        uint256 indexed id,
        address indexed harpoon,
        address indexed creator,
        HarpoonParams params
    );
    
    event HarpoonClosed(uint256 indexed id, address indexed closer, uint256 pnl);
    event CCIPMessageReceived(address indexed sender, bytes4 indexed selector);
    event PlatformAdded(string platform, address router);
    event PlatformRemoved(string platform);
    event CreationFeeUpdated(uint256 oldFee, uint256 newFee);
    event TrustedSenderUpdated(address oldSender, address newSender);

    // 
    //                             MODIFIERS
    // 
    
    modifier onlyNFTHolder(address user) {
        require(hasEligibleNFT(user), "No eligible NFT");
        _;
    }
    
    modifier onlyCCIP() {
        require(msg.sender == address(ccipRouter), "Not CCIP router");
        _;
    }
    
    modifier validHarpoonParams(HarpoonParams calldata params) {
        require(params.collateralAmount >= MIN_COLLATERAL, "Insufficient collateral");
        require(params.leverage > 0 && params.leverage <= MAX_LEVERAGE, "Invalid leverage");
        require(params.duration > 0 && params.duration <= MAX_DURATION, "Invalid duration");
        require(supportedPlatforms[params.platform], "Unsupported platform");
        require(params.slippageBps <= 1000, "Slippage too high"); // Max 10%
        _;
    }

    // 
    //                             CONSTRUCTOR
    // 
    
    constructor(
        address _ccipRouter,
        uint64 _ethereumChainSelector,
        address _surfBoardNFT,
        address _mumuFrensNFT,
        address _feeRecipient
    ) Ownable(msg.sender) {
        require(_ccipRouter != address(0), "Invalid CCIP router");
        require(_surfBoardNFT != address(0), "Invalid SURF Board NFT");
        require(_mumuFrensNFT != address(0), "Invalid mumu-frens NFT");
        require(_feeRecipient != address(0), "Invalid fee recipient");
        
        ccipRouter = ICCIPRouter(_ccipRouter);
        ethereumChainSelector = _ethereumChainSelector;
        surfBoardNFT = IERC721(_surfBoardNFT);
        mumuFrensNFT = IERC721(_mumuFrensNFT);
        feeRecipient = _feeRecipient;
        
        // Deploy Harpoon implementation
        harpoonImplementation = address(new Harpoon());
        
        // Add supported platforms
        supportedPlatforms["GMX"] = true;
        supportedPlatforms["UNISWAP"] = true;
    }

    // 
    //                          CORE FUNCTIONS
    // 
    
    /**
     * @notice Create a Harpoon from cross-chain CCIP call
     * @param params Harpoon parameters
     * @param creator Original creator from Ethereum
     * @return harpoon Address of created Harpoon contract
     */
    function createHarpoonFromCCIP(
        HarpoonParams calldata params,
        address creator
    ) 
        external 
        onlyCCIP 
        whenNotPaused
        validHarpoonParams(params)
        returns (address harpoon) 
    {
        // Verify creator has eligible NFT (would need cross-chain verification)
        // For now, we trust the Ethereum side verification
        
        return _createHarpoon(params, creator);
    }
    
    /**
     * @notice Create a Harpoon directly on Arbitrum (for NFT holders)
     * @param params Harpoon parameters
     * @return harpoon Address of created Harpoon contract
     */
    function createHarpoon(HarpoonParams calldata params) 
        external 
        payable
        nonReentrant
        whenNotPaused
        onlyNFTHolder(msg.sender)
        validHarpoonParams(params)
        returns (address harpoon) 
    {
        require(msg.value >= creationFee, "Insufficient creation fee");
        require(userHarpoonCount[msg.sender] < MAX_HARPOONS_PER_USER, "Too many harpoons");
        
        // Transfer creation fee
        if (msg.value > 0) {
            payable(feeRecipient).transfer(msg.value);
        }
        
        return _createHarpoon(params, msg.sender);
    }
    
    /**
     * @notice Internal function to create a Harpoon
     * @param params Harpoon parameters
     * @param creator Creator address
     * @return harpoon Address of created Harpoon contract
     */
    function _createHarpoon(
        HarpoonParams calldata params,
        address creator
    ) internal returns (address harpoon) {
        // Create deterministic salt for CREATE2
        bytes32 salt = keccak256(abi.encodePacked(creator, harpoonCount, block.timestamp));
        
        // Deploy Harpoon clone
        harpoon = Clones.cloneDeterministic(harpoonImplementation, salt);
        
        // Convert HarpoonParams to PositionParams
        Harpoon.PositionParams memory positionParams = Harpoon.PositionParams({
            targetToken: params.targetToken,
            collateralAmount: params.collateralAmount,
            leverage: params.leverage,
            isLong: params.isLong,
            slippageBps: params.slippageBps,
            platformName: params.platform,
            duration: params.duration,
            platformSpecificData: params.platformSpecificData
        });
        
        // Initialize Harpoon
        Harpoon(payable(harpoon)).initialize(
            creator,
            address(surfBoardNFT),
            address(mumuFrensNFT),
            positionParams,
            platformRouters[params.platform]
        );
        
        // Update mappings
        uint256 harpoonId = harpoonCount++;
        harpoons[harpoonId] = harpoon;
        userHarpoons[creator].push(harpoonId);
        userHarpoonCount[creator]++;
        
        emit HarpoonCreated(harpoonId, harpoon, creator, params);
        
        return harpoon;
    }

    // 
    //                           CCIP FUNCTIONS
    // 
    
    /**
     * @notice Receive CCIP messages from Ethereum
     * @param message Encoded CCIP message
     */
    function ccipReceive(bytes calldata message) external override onlyCCIP {
        CCIPMessage memory ccipMsg = abi.decode(message, (CCIPMessage));
        
        // Verify sender is trusted
        require(ccipMsg.sender == trustedEthereumSender, "Untrusted sender");
        
        // Route message based on selector
        if (ccipMsg.selector == this.createHarpoonFromCCIP.selector) {
            (HarpoonParams memory params, address creator) = abi.decode(
                ccipMsg.data, 
                (HarpoonParams, address)
            );
            this.createHarpoonFromCCIP(params, creator);
        }
        
        emit CCIPMessageReceived(ccipMsg.sender, ccipMsg.selector);
    }

    // 
    //                           VIEW FUNCTIONS
    // 
    
    /**
     * @notice Check if user has eligible NFT
     * @param user User address
     * @return True if user owns SURF Board or mumu-frens NFT
     */
    function hasEligibleNFT(address user) public view returns (bool) {
        return surfBoardNFT.balanceOf(user) > 0 || mumuFrensNFT.balanceOf(user) > 0;
    }
    
    /**
     * @notice Get Harpoon address by ID
     * @param id Harpoon ID
     * @return Harpoon contract address
     */
    function getHarpoon(uint256 id) external view returns (address) {
        require(id < harpoonCount, "Invalid harpoon ID");
        return harpoons[id];
    }
    
    /**
     * @notice Get user's Harpoon IDs
     * @param user User address
     * @return Array of Harpoon IDs
     */
    function getUserHarpoons(address user) external view returns (uint256[] memory) {
        return userHarpoons[user];
    }
    
    /**
     * @notice Get Harpoon details
     * @param id Harpoon ID
     * @return harpoon Harpoon address
     * @return creator Creator address
     * @return status Current status
     */
    function getHarpoonDetails(uint256 id) external view returns (
        address harpoon,
        address creator,
        Harpoon.Status status
    ) {
        require(id < harpoonCount, "Invalid harpoon ID");
        harpoon = harpoons[id];
        Harpoon harpoonContract = Harpoon(payable(harpoon));
        creator = harpoonContract.creator();
        status = harpoonContract.status();
    }
    
    /**
     * @notice Check if platform is supported
     * @param platform Platform name
     * @return True if platform is supported
     */
    function isPlatformSupported(string calldata platform) external view returns (bool) {
        return supportedPlatforms[platform];
    }

    // 
    //                          ADMIN FUNCTIONS
    // 
    
    /**
     * @notice Add supported trading platform
     * @param platform Platform name (e.g., "GMX", "UNISWAP")
     * @param router Platform router address
     */
    function addPlatform(string calldata platform, address router) external onlyOwner {
        require(router != address(0), "Invalid router");
        supportedPlatforms[platform] = true;
        platformRouters[platform] = router;
        emit PlatformAdded(platform, router);
    }
    
    /**
     * @notice Remove supported trading platform
     * @param platform Platform name
     */
    function removePlatform(string calldata platform) external onlyOwner {
        supportedPlatforms[platform] = false;
        delete platformRouters[platform];
        emit PlatformRemoved(platform);
    }
    
    /**
     * @notice Set trusted Ethereum sender for CCIP
     * @param _trustedSender Ethereum LPVault or router address
     */
    function setTrustedEthereumSender(address _trustedSender) external onlyOwner {
        address oldSender = trustedEthereumSender;
        trustedEthereumSender = _trustedSender;
        emit TrustedSenderUpdated(oldSender, _trustedSender);
    }
    
    /**
     * @notice Update creation fee
     * @param _newFee New creation fee in wei
     */
    function setCreationFee(uint256 _newFee) external onlyOwner {
        uint256 oldFee = creationFee;
        creationFee = _newFee;
        emit CreationFeeUpdated(oldFee, _newFee);
    }
    
    /**
     * @notice Update fee recipient
     * @param _newRecipient New fee recipient address
     */
    function setFeeRecipient(address _newRecipient) external onlyOwner {
        require(_newRecipient != address(0), "Invalid recipient");
        feeRecipient = _newRecipient;
    }
    
    /**
     * @notice Update Harpoon implementation (for upgrades)
     * @param _newImplementation New Harpoon implementation address
     */
    function setHarpoonImplementation(address _newImplementation) external onlyOwner {
        require(_newImplementation != address(0), "Invalid implementation");
        harpoonImplementation = _newImplementation;
    }
    
    /**
     * @notice Pause the factory
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @notice Unpause the factory
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @notice Emergency withdraw ETH
     */
    function emergencyWithdrawETH() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
    
    /**
     * @notice Receive function for ETH deposits
     */
    receive() external payable {}
}

project/contracts/LPVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title LPVault
 * @notice Users deposit SURF/WETH Uniswap V2 LP tokens and earn USDC rewards
 * @dev Implements a share-based reward distribution system
 */
contract LPVault is ReentrancyGuard, Pausable, Ownable {
    using SafeERC20 for IERC20;
    using Math for uint256;

    // 
    //                             CONSTANTS
    // 
    
    uint256 private constant PRECISION = 1e30;
    uint256 public constant EMERGENCY_WITHDRAW_FEE_BPS = 100; // 1%
    uint256 public constant MAX_BPS = 10000;
    uint256 public constant MIN_DEPOSIT = 1e15; // 0.001 LP tokens

    // 
    //                             STORAGE
    // 
    
    IERC20 public immutable lpToken;   // SURF/WETH LP token
    IERC20 public immutable usdc;      // Reward token
    
    uint256 public totalShares;        // Total shares outstanding
    uint256 public accUSDCPerShare;    // Accumulated USDC per share (scaled by PRECISION)
    uint256 public lastRewardBlock;    // Last block when rewards were distributed
    
    // Emergency withdraw fee recipient
    address public feeRecipient;
    
    // Reward distribution
    address public rewardDistributor;
    uint256 public totalRewardsDistributed;
    
    struct UserInfo {
        uint256 shares;                // User's share in the pool
        uint256 rewardDebt;           // Reward debt for accurate reward calculation
        uint256 lastDepositBlock;     // Last deposit block (for potential time locks)
    }
    
    mapping(address => UserInfo) public users;

    // 
    //                             EVENTS
    // 
    
    event Deposit(address indexed user, uint256 lpAmount, uint256 shares);
    event Withdraw(address indexed user, uint256 shares, uint256 lpAmount);
    event EmergencyWithdraw(address indexed user, uint256 lpAmount, uint256 fee);
    event Claim(address indexed user, uint256 amount);
    event RewardNotified(uint256 amount, address indexed distributor);
    event RewardDistributorChanged(address indexed oldDistributor, address indexed newDistributor);
    event FeeRecipientChanged(address indexed oldRecipient, address indexed newRecipient);

    // 
    //                             MODIFIERS
    // 
    
    modifier onlyRewardDistributor() {
        require(msg.sender == rewardDistributor || msg.sender == owner(), "Not authorized");
        _;
    }

    modifier updateReward(address user) {
        if (user != address(0)) {
            users[user].rewardDebt = users[user].shares * accUSDCPerShare / PRECISION;
        }
        _;
    }

    // 
    //                             CONSTRUCTOR
    // 
    
    /**
     * @notice Constructor
     * @param _lpToken SURF/WETH LP token address
     * @param _usdc USDC token address
     * @param _feeRecipient Emergency withdraw fee recipient
     */
    constructor(
        address _lpToken,
        address _usdc,
        address _feeRecipient
    ) Ownable(msg.sender) {
        require(_lpToken != address(0), "Invalid LP token");
        require(_usdc != address(0), "Invalid USDC token");
        require(_feeRecipient != address(0), "Invalid fee recipient");
        
        lpToken = IERC20(_lpToken);
        usdc = IERC20(_usdc);
        feeRecipient = _feeRecipient;
        rewardDistributor = msg.sender;
        lastRewardBlock = block.number;
    }

    // 
    //                          CORE USER FUNCTIONS
    // 
    
    /**
     * @notice Deposit LP tokens to earn USDC rewards
     * @param lpAmount Amount of LP tokens to deposit
     */
    function deposit(uint256 lpAmount) external nonReentrant whenNotPaused updateReward(msg.sender) {
        require(lpAmount >= MIN_DEPOSIT, "Deposit too small");
        
        UserInfo storage user = users[msg.sender];
        
        // Calculate shares to mint
        uint256 shares;
        if (totalShares == 0) {
            shares = lpAmount;
        } else {
            shares = lpAmount * totalShares / lpBalance();
        }
        
        require(shares > 0, "No shares minted");
        
        // Update state
        user.shares += shares;
        user.lastDepositBlock = block.number;
        totalShares += shares;
        
        // Transfer LP tokens
        lpToken.safeTransferFrom(msg.sender, address(this), lpAmount);
        
        emit Deposit(msg.sender, lpAmount, shares);
    }
    
    /**
     * @notice Withdraw LP tokens by burning shares
     * @param shareAmount Amount of shares to burn
     */
    function withdraw(uint256 shareAmount) external nonReentrant updateReward(msg.sender) {
        UserInfo storage user = users[msg.sender];
        require(shareAmount > 0, "Invalid share amount");
        require(user.shares >= shareAmount, "Insufficient shares");
        
        // Calculate LP tokens to return
        uint256 lpAmount = shareAmount * lpBalance() / totalShares;
        require(lpAmount > 0, "No LP tokens to withdraw");
        
        // Update state
        user.shares -= shareAmount;
        totalShares -= shareAmount;
        
        // Transfer LP tokens
        lpToken.safeTransfer(msg.sender, lpAmount);
        
        emit Withdraw(msg.sender, shareAmount, lpAmount);
    }
    
    /**
     * @notice Claim accumulated USDC rewards
     */
    function claimRewards() external nonReentrant updateReward(msg.sender) {
        uint256 rewards = pendingRewards(msg.sender);
        if (rewards > 0) {
            users[msg.sender].rewardDebt = users[msg.sender].shares * accUSDCPerShare / PRECISION;
            usdc.safeTransfer(msg.sender, rewards);
            emit Claim(msg.sender, rewards);
        }
    }
    
    /**
     * @notice Emergency withdraw all LP tokens (with fee)
     */
    function emergencyWithdraw() external nonReentrant {
        UserInfo storage user = users[msg.sender];
        uint256 userShares = user.shares;
        require(userShares > 0, "No shares to withdraw");
        
        // Calculate LP tokens
        uint256 lpAmount = userShares * lpBalance() / totalShares;
        uint256 fee = lpAmount * EMERGENCY_WITHDRAW_FEE_BPS / MAX_BPS;
        uint256 userAmount = lpAmount - fee;
        
        // Reset user
        user.shares = 0;
        user.rewardDebt = 0;
        totalShares -= userShares;
        
        // Transfer tokens
        if (userAmount > 0) {
            lpToken.safeTransfer(msg.sender, userAmount);
        }
        if (fee > 0) {
            lpToken.safeTransfer(feeRecipient, fee);
        }
        
        emit EmergencyWithdraw(msg.sender, userAmount, fee);
    }

    // 
    //                          REWARD DISTRIBUTION
    // 
    
    /**
     * @notice Distribute USDC rewards to all LP stakers
     * @param usdcAmount Amount of USDC to distribute
     */
    function notifyRewardAmount(uint256 usdcAmount) external onlyRewardDistributor {
        require(usdcAmount > 0, "No rewards to distribute");
        require(totalShares > 0, "No shares to distribute to");
        
        // Transfer USDC from distributor
        usdc.safeTransferFrom(msg.sender, address(this), usdcAmount);
        
        // Update accumulated rewards per share
        accUSDCPerShare += usdcAmount * PRECISION / totalShares;
        totalRewardsDistributed += usdcAmount;
        lastRewardBlock = block.number;
        
        emit RewardNotified(usdcAmount, msg.sender);
    }

    // 
    //                           VIEW FUNCTIONS
    // 
    
    /**
     * @notice Calculate pending USDC rewards for a user
     * @param user User address
     * @return Pending USDC rewards
     */
    function pendingRewards(address user) public view returns (uint256) {
        UserInfo memory userInfo = users[user];
        if (userInfo.shares == 0) {
            return 0;
        }
        
        uint256 totalRewards = userInfo.shares * accUSDCPerShare / PRECISION;
        if (totalRewards <= userInfo.rewardDebt) {
            return 0;
        }
        
        return totalRewards - userInfo.rewardDebt;
    }
    
    /**
     * @notice Get current LP token balance of the contract
     * @return LP token balance
     */
    function lpBalance() public view returns (uint256) {
        return lpToken.balanceOf(address(this));
    }
    
    /**
     * @notice Get user info
     * @param user User address
     * @return shares User's shares
     * @return rewardDebt User's reward debt
     * @return pendingUSDC Pending USDC rewards
     */
    function getUserInfo(address user) external view returns (
        uint256 shares,
        uint256 rewardDebt,
        uint256 pendingUSDC
    ) {
        UserInfo memory userInfo = users[user];
        return (
            userInfo.shares,
            userInfo.rewardDebt,
            pendingRewards(user)
        );
    }
    
    /**
     * @notice Calculate LP tokens for a given amount of shares
     * @param shareAmount Amount of shares
     * @return LP token amount
     */
    function sharesToLP(uint256 shareAmount) external view returns (uint256) {
        if (totalShares == 0) {
            return shareAmount;
        }
        return shareAmount * lpBalance() / totalShares;
    }
    
    /**
     * @notice Calculate shares for a given amount of LP tokens
     * @param lpAmount Amount of LP tokens
     * @return Share amount
     */
    function lpToShares(uint256 lpAmount) external view returns (uint256) {
        if (totalShares == 0) {
            return lpAmount;
        }
        return lpAmount * totalShares / lpBalance();
    }

    // 
    //                         ADMIN FUNCTIONS
    // 
    
    /**
     * @notice Set reward distributor address
     * @param _rewardDistributor New reward distributor address
     */
    function setRewardDistributor(address _rewardDistributor) external onlyOwner {
        require(_rewardDistributor != address(0), "Invalid address");
        address oldDistributor = rewardDistributor;
        rewardDistributor = _rewardDistributor;
        emit RewardDistributorChanged(oldDistributor, _rewardDistributor);
    }
    
    /**
     * @notice Set fee recipient address
     * @param _feeRecipient New fee recipient address
     */
    function setFeeRecipient(address _feeRecipient) external onlyOwner {
        require(_feeRecipient != address(0), "Invalid address");
        address oldRecipient = feeRecipient;
        feeRecipient = _feeRecipient;
        emit FeeRecipientChanged(oldRecipient, _feeRecipient);
    }
    
    /**
     * @notice Pause the contract
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @notice Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @notice Emergency function to recover stuck tokens (not LP or USDC)
     * @param token Token address to recover
     * @param amount Amount to recover
     */
    function recoverToken(address token, uint256 amount) external onlyOwner {
        require(token != address(lpToken), "Cannot recover LP token");
        require(token != address(usdc), "Cannot recover USDC");
        IERC20(token).safeTransfer(owner(), amount);
    }
}
